mapper.select:
SELECT `id`   AS `id`,
       `text` As `text`
FROM `campmoa-bbs`.`boards`
WHERE BINARY `id` = #{id}
LIMIT 1;

SELECT `index` AS `index`, `board_value` AS `boardValue`, `user_email` AS `userEamil`, `title` AS `title`, `content` AS `content`, `view` AS `view`, `created_at` AS `createdAt`, `users`.`name` AS `userName` FROM `campmoa-bbs`.`articles` LEFT JOIN `campmoa_member`.`users` ON `articles`.`user_email` = `users`.`email` WHERE `board_value` = #{boardId}
AND REPLACE(`articles`.`title`,' ', '') LIKE CONCAT('%', REPLACE(#{keyword}, ' ', ''),'%')
AND REPLACE(`articles`.`title`,' ', '') LIKE CONCAT('%', REPLACE(#{keyword}, ' ', ''),'%') OR REPLACE(`articles`.`content`,' ', '') LIKE CONCAT('%', REPLACE(#{keyword}, ' ', ''),'%')
AND BINARY `users`.`name` = #{keyword}
GROUP BY `articles`.`index` ORDER BY `articles`.`index` desc LIMIT #{limit} OFFSET #{offset};

SELECT * FROM `campmoa-bbs`.`articles` LEFT JOIN `campmoa_member`.`users` ON articles.user_email = users.email WHERE board_value = #{boardId}
AND (title LIKE CONCAT('%',#{keyword},'%') OR `users`.`name` LIKE CONCAT('%',#{keyword},'%'))
AND title LIKE CONCAT('%',#{keyword},'%')
AND `users`.`name` LIKE CONCAT('%',#{keyword},'%')
ORDER BY `articles`.`index` desc LIMIT ${offset}, ${limit}
